mapper.sql:
SELECT u.id, u.username, u.email, u.mobile, u.status, u.create_time createTime, u.sex, u.last_login_time lastLoginTime, u.modify_time modifyTime, u.description, u.avatar, u.salt, GROUP_CONCAT(r.role_id) roleId, GROUP_CONCAT(r.ROLE_NAME) roleName FROM t_user u LEFT JOIN t_user_role ur ON (u.id = ur.user_id) LEFT JOIN t_role r ON r.role_id = ur.role_id
AND u.username = #{user.username}
AND u.sex = #{user.sex}
AND u.status = #{user.status}
AND u.mobile = #{user.mobile}
And u.create_time > #{user.createTimeFrom}
And u.create_time < #{user.createTimeTo}
GROUP BY u.username, u.id, u.email, u.mobile, u.status, u.create_time, u.sex, u.salt, u.last_login_time, u.modify_time, u.description, u.avatar
